import { useEffect, useState } from 'react';
import './bookingForm.css';
import { DateRange } from 'react-date-range';
import axios from '../../utils/axios';
import { useParams } from 'react-router-dom';

function BookingForm() {
  const [room, setRoom] = useState([]);
  const hotelId = useParams().id;

  useEffect(() => {
    async function fetchData() {
      const request = await axios.get(`/rooms?hotel=${hotelId}`);
      setRoom(request.data);
      return request;
    }
    fetchData();
  }, [hotelId]);

  console.log(hotelId);

  return (
    <div className="booking">
      <div className="bookingContainer">
        <div className="calen">
          <h2>Dates</h2>
          <DateRange
            editableDateInputs={true}
            moveRangeOnFirstSelection={false}
            minDate={new Date()}
          />
        </div>
        <div className="bookingWrapper">
          <h2>Reserve Info</h2>
          <div className="bookingInputContainer">
            <label htmlFor="fullname">Your Full Name: </label>
            <input type="text" placeholder="Full Name" />
          </div>
          <div className="bookingInputContainer">
            <label htmlFor="fullname">Your Email: </label>
            <input type="text" placeholder="Email" />
          </div>
          <div className="bookingInputContainer">
            <label htmlFor="fullname">Your Phone Number: </label>
            <input type="text" placeholder="Phone Number" />
          </div>
          <div className="bookingInputContainer">
            <label htmlFor="fullname">Your Identity Card Number: </label>
            <input type="text" placeholder="Card Number" />
          </div>
        </div>
      </div>
      <h2>Select Rooms</h2>
      <div className="bookingContainer">
        {room.length > 0
          ? room.map(r => {
              <div className="bookingContent">
                <div className="bookingDetails">
                  <h3>Budget Double Room</h3>
                  <p>Pay nothing until September 04, 2022</p>
                  <p>Max People: 2</p>
                  <p>$350</p>
                </div>
                <div className="bookingCheckBox">
                  <div className="checkbox-container">
                    <p>101</p>
                    <input type="checkbox" />
                  </div>
                  <div className="checkbox-container">
                    <p>101</p>
                    <input type="checkbox" />
                  </div>
                  <div className="checkbox-container">
                    <p>101</p>
                    <input type="checkbox" />
                  </div>
                  <div className="checkbox-container">
                    <p>101</p>
                    <input type="checkbox" />
                  </div>
                </div>
              </div>;
            })
          : ''}
      </div>
      <h2>Total Bill: $700</h2>
      <div className="bookingContainer"></div>
    </div>
  );
}

export default BookingForm;
