import './addHotel.css';
import { useEffect, useState } from 'react';
import axios from '../../utils/axios';

function AddHotel() {
  const [data, setData] = useState({
    name: 'My Hotel',
    city: 'Ha Noi',
    distance: '500',
    desc: 'Description',
    photos: [],
    type: 'hotel',
    address: '74 Chien Thang',
    title: 'The Best Hotel',
    price: '100',
    featured: false,
    rooms: [],
  });
  const [rooms, setRooms] = useState([]);
  const [selectedOptions, setSelectedOptions] = useState([]);

  useEffect(() => {
    async function fetchData() {
      const request = await axios.get('/all-rooms');
      setRooms(request.data);
      return request;
    }
    fetchData();
  });

  const handleChange = e => {
    e.preventDefault();
    setData({ ...data, [e.target.id]: e.target.value });
  };

  const handleRoomSelect = e => {
    e.preventDefault();
    if (data.rooms.includes(e.target.value) !== -1)
      setData({ ...data, [e.target.id]: [...data.rooms, e.target.value] });
    else
      setData({
        [e.target.id]: data.rooms.filter(room => room !== e.target.value),
      });

    console.log(data.rooms);
  };

  // const handleRoomSelect = e => {
  //   const value = e.target.value;
  //   console.log(value);
  //   if (selectedOptions.includes(value)) {
  //     setSelectedOptions(selectedOptions.filter(option => option !== value));
  //   } else {
  //     setSelectedOptions([...selectedOptions, value]);
  //   }
  // };

  const handleSubmit = () => {
    console.log(data);
  };

  return (
    <div className="content">
      <div className="card-large shadow ">
        <div className="add-form">
          <div className="column">
            <div className="form-group">
              <label htmlFor="name">Name</label>
              <input
                type="text"
                id="name"
                value={data.name}
                onChange={handleChange}
              />
            </div>

            <div className="form-group">
              <label htmlFor="city">City</label>
              <input
                type="text"
                id="city"
                value={data.city}
                onChange={handleChange}
              />
            </div>

            <div className="form-group">
              <label htmlFor="distance">Distance from City center</label>
              <input
                type="text"
                id="distance"
                value={data.distance}
                onChange={handleChange}
              />
            </div>

            <div className="form-group">
              <label htmlFor="description">Description</label>
              <input
                type="text"
                id="desc"
                value={data.desc}
                onChange={handleChange}
              />
            </div>

            <div className="form-group">
              <label htmlFor="image">Image</label>
              <input type="file" id="image" multiple />
            </div>
          </div>
          <div className="column">
            <div className="form-group">
              <label htmlFor="type">Type</label>
              <input
                type="text"
                id="type"
                value={data.type}
                onChange={handleChange}
              />
            </div>
            <div className="form-group">
              <label htmlFor="address">Address</label>
              <input
                type="text"
                id="address"
                value={data.address}
                onChange={handleChange}
              />
            </div>
            <div className="form-group">
              <label htmlFor="title">Title</label>
              <input
                type="text"
                id="title"
                value={data.title}
                onChange={handleChange}
              />
            </div>
            <div className="form-group">
              <label htmlFor="price">Price</label>
              <input
                type="text"
                id="price"
                value={data.price}
                onChange={handleChange}
              />
            </div>
            <div className="form-group">
              <label htmlFor="featured">Featured</label>
              <select
                id="featured"
                value={data.featured}
                onChange={handleChange}
              >
                <option value={true}>Yes</option>
                <option value={false}>No</option>
              </select>
            </div>
          </div>
        </div>
        <div className="room-list">
          <div class="checkbox-list">
            <div class="scrollable-list">
              <label>
                <input type="checkbox" value="1" />
                Option 1
              </label>
              <label>
                <input type="checkbox" value="2" />
                Option 2
              </label>
              <label>
                <input type="checkbox" value="3" />
                Option 3
              </label>
            </div>
          </div>
        </div>
        <div className="room-list">
          <button onClick={handleSubmit}>Send</button>
        </div>
      </div>
    </div>
  );
}

export default AddHotel;
