import { useEffect, useState } from 'react';
import axios from '../../utils/axios';

function AddRoom() {
  const [data, setData] = useState({
    title: '2 Bed Room',
    price: 100,
    desc: 'King size bed, 1 bathroom',
    maxPeople: 2,
  });
  const [hotels, setHotels] = useState([]);

  useEffect(() => {
    async function fetchData() {
      const request = await axios.get('/all-hotels');
      setHotels(request.data);
      return request;
    }
    fetchData();
  });

  return (
    <div className="content">
      <div className="card-large shadow ">
        <div className="add-form">
          <div className="column">
            <div className="form-group">
              <label htmlFor="title">Title</label>
              <input
                type="text"
                id="title"
                value={data.title}
                // onChange={handleChange}
              />
            </div>
            <div className="form-group">
              <label htmlFor="price">Price</label>
              <input
                type="text"
                id="price"
                value={data.price}
                // onChange={handleChange}
              />
            </div>
            <div className="form-group">
              <label htmlFor="roomNumbers">Room Numbers</label>
              <textarea id="roomNumbers" cols="30" rows="3">
                Hello
              </textarea>
            </div>
          </div>

          <div className="column">
            <div className="form-group">
              <label htmlFor="desc">Description</label>
              <input
                type="text"
                id="desc"
                value={data.desc}
                // onChange={handleChange}
              />
            </div>
            <div className="form-group">
              <label htmlFor="maxPeople">Max People</label>
              <input
                type="text"
                id="maxPeople"
                value={data.maxPeople}
                // onChange={handleChange}
              />
            </div>
            <div className="form-group">
              <label htmlFor="hotel">Choose A Hotel</label>
              <select id="hotel">
                <option value="1">One</option>
                <option value="2">Two</option>
                <option value="3">Three</option>
                <option value="4">Four</option>
              </select>
            </div>
          </div>
        </div>
        <div className="room-list"></div>
        <div className="room-list">
          <button>Send</button>
        </div>
      </div>
    </div>
  );
}

export default AddRoom;
