import { useEffect, useState } from 'react';
import axios from '../../utils/axios';

function AddRoom() {
  const [data, setData] = useState({
    title: '2 Bed Room',
    price: 100,
    desc: 'King size bed, 1 bathroom',
    maxPeople: 2,
    roomNumbers: [],
  });
  const [hotels, setHotels] = useState([]);

  useEffect(() => {
    async function fetchData() {
      const request = await axios.get('/all-hotels');
      setHotels(request.data);
      return request;
    }
    fetchData();
  });

  const handleChange = e => {
    e.preventDefault();
    setData({ ...data, [e.target.id]: e.target.value });
  };

  const handleTextArea = e => {
    const value = e.target.value;
    const numbers = value.replace(/\s/g, '').split(',').map(Number);
    console.log(numbers);

    if (numbers.some(NaN) === -1) setData({ ...data, roomNumbers: numbers });
    else {
      alert('Hãy điền giá trị hợp lệ !!');
      return;
    }
  };

  const handleSubmit = () => {
    console.log(data);
  };

  return (
    <div className="content">
      <div className="card-large shadow ">
        <h1>Thêm Phòng</h1>
        <div className="add-form">
          <div className="column">
            <div className="form-group">
              <label htmlFor="title">Title</label>
              <input
                type="text"
                id="title"
                value={data.title}
                onChange={handleChange}
              />
            </div>
            <div className="form-group">
              <label htmlFor="price">Price</label>
              <input
                type="text"
                id="price"
                value={data.price}
                onChange={handleChange}
              />
            </div>
            <div className="form-group">
              <label htmlFor="roomNumbers">Room Numbers</label>
              <textarea
                id="roomNumbers"
                cols="30"
                rows="2"
                placeholder="give comma between room numbers. Ex: 101, 202, ..."
                onChange={handleTextArea}
              ></textarea>
            </div>
          </div>

          <div className="column">
            <div className="form-group">
              <label htmlFor="desc">Description</label>
              <input
                type="text"
                id="desc"
                value={data.desc}
                onChange={handleChange}
              />
            </div>
            <div className="form-group">
              <label htmlFor="maxPeople">Max People</label>
              <input
                type="text"
                id="maxPeople"
                value={data.maxPeople}
                onChange={handleChange}
              />
            </div>
            <div className="form-group">
              <label htmlFor="hotel">Choose A Hotel</label>
              <select id="hotel">
                {hotels.map((hotel, i) => {
                  return (
                    <option key={i} value={hotel._id}>
                      {hotel.name}
                    </option>
                  );
                })}
              </select>
            </div>
          </div>
        </div>
        <div className="room-list"></div>
        <div className="room-list">
          <button onClick={handleSubmit}>Send</button>
        </div>
      </div>
    </div>
  );
}

export default AddRoom;
